public class X{

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  
	int size1 = 13;	
	for (int i = 0 ; i<size1 ; i++) {
	  for(int j =0; j<size1 ; j++) {
			if(i==j || j== size1 - i-1) 
				System.out.print("*");
				
			else 
			System.out.print(" ");	
	  }
	System.out.println();		
  }
  
  }
	}

